import React from "react";

import { getPlayers } from "../../../axios/axios";
import Card from "../../../components/Card/Card";

class PlayerPage extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            playerPage: null
        }
    }

    getAge = (dob) => {
        let milli = new Date() - new Date(dob)
        return Math.floor(milli / 1000 / 60 / 60 / 24 / 365);
    }

    getPlayersCallback = (data) => {
        let playerPage = []
        data.forEach(element => {
            playerPage.push(
                <Card key={element.email} styleName="cardWhite round relative shadow">
                    <div>
                        <div>
                            {element.forename}, {element.middleNames}, {element.surname}
                        </div>
                        <div>
                            age: {this.getAge(element.dateOfBirth)}
                        </div>
                        <div>
                            ELO: {element.elo}
                        </div>
                    </div>
                </Card>
            )
        });

        this.setState({
            playerPage: playerPage
        })
    }

    componentDidMount() {
        getPlayers(this.getPlayersCallback)
    }

    render() {
        return (
            <div>
                {this.state.playerPage}
            </div>
        )
    }

}

export default PlayerPage;
