import React from "react";

import { getMatchWithNames } from "../../../axios/axios";
import styles from './MatchPage.module.css'

import Card from "../../../components/Card/Card";

class MatchPage extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            matchPage: null
        }
    }

    getMatchWithNamesCallback = (data) => {
        let matchPage = []
        for (let i = 0; i < data.length; i += 2) {
            matchPage.push(
                <Card key={i} styleName="cardWhite round relative shadow" inlineStyles={{ marginLeft: "auto", marginRight: "auto", width: "75%", marginBottom: "50px", display: "flex", flexDirection: "column" }}>
                    <div>
                        <div>{data[i].fullName}</div>
                        <div>{data[i + 1].fullName}</div>
                    </div>
                </Card>
            )
        }

        this.setState({
            matchPage: matchPage
        })
    }

    componentDidMount() {
        getMatchWithNames(this.getMatchWithNamesCallback, "");
    }

    render() {
        return (
            <div className={styles.match}>
                <div className={styles.matchPageHeader}>Matches</div>
                {this.state.matchPage}
            </div>
        )
    }

}

export default MatchPage;
