import { Card } from "@mui/material";
import { CardContent } from "@mui/material";
import { Modal } from "@mui/material";
import { InputLabel } from "@mui/material";
import { FormControl } from "@mui/material";
import Select from '@mui/material/Select';

import { Button } from "@mui/material";
import { TextField } from "@mui/material";
import React from "react";
import { getSimplePlayers, getLastMatchID, getVenues, getCourtWithVenue } from "../../../axios/axios";

import styles from './AdminPage.module.css'
import { MenuItem } from "@mui/material";


class AdminPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            player: false,
            match: false,
            court: false,
            venue: false,

            phoneHandler: [],

            email: "",
            forename: "",
            middlenames: "",
            surname: "",
            dateofbirth: "",
            phones: [
                {
                    number: "",
                    type: "",
                }
            ],

            playersList: "",
            matchP1: "",
            matchP2: "",
            matchIndex: 0,
            venuesList: "",
            venueValue: "",
            courtList: "",
            courtValue: "",
            courtDisabled: true,
        }
    }

    inputHandler = (event, toChange) => {
        this.setState({
            [toChange]: event.target.value
        })
        console.log(this.state)
    }

    phoneHandler = (event, phoneIndex) => {
        let phones = [...this.state.phones];

        phones[phoneIndex].number = event.target.value;

        this.setState({
            phones: [...phones]
        })
    }

    phoneTypeHandler = (event, index, phoneIndex) => {
        console.log(index)
        let phoneHandler = [...this.state.phoneHandler];

        let phones = [...this.state.phones];

        phones[phoneIndex].type = event.target.value;

        this.setState({
            phones: [...phones],
            phoneHandler: phoneHandler
        })
    }

    addNewPhone = () => {
        let phoneHandler = [...this.state.phoneHandler];
        let newIndex = this.state.phoneHandler.length + 1;
        phoneHandler.push(
            <div key={newIndex}>
                <TextField
                    label={"phone" + newIndex}
                    margin="normal"
                    color="success"
                    onChange={(event) => this.phoneHandler(event, newIndex - 1)}
                />
                <FormControl key={"phone" + newIndex - 1} fullWidth>
                    <InputLabel id={"phone" + (newIndex - 1)}>Phone{newIndex} Type</InputLabel>
                    <Select key="test" labelId={"phone-type" + newIndex} label={"Phone" + (newIndex) + " Type"} value={this.state.phones[newIndex - 1].type} onChange={(event, index) => this.phoneTypeHandler(event, index, newIndex - 1)}>
                        <MenuItem value={"home"}>Home</MenuItem>
                        <MenuItem value={"mobile"}>Mobile</MenuItem>
                        <MenuItem value={"work"}>Work</MenuItem>
                    </Select>
                </FormControl>
            </div>
        )

        this.setState(
            {
                phoneHandler: phoneHandler,
                phones: [
                    ...this.state.phones,
                    {
                        phone: "",
                        type: ""
                    }
                ],
            }
        )
    }

    user1Handler = (event) => {
        this.setState({
            matchP1: event.target.value
        })
    }

    user2Handler = (event) => {
        this.setState({
            matchP2: event.target.value
        })
    }

    getCourtWithVenueCallback = (data) => {
        let courtList = []

        data.forEach(element => {
            courtList.push(
                <MenuItem key={element.number} value={element.number.toString()}>{element.number.toString()} ({element.surface})</MenuItem>
            )
        })


        let courtValue;
        let courtDisabled;

        if (courtList.length === 0) {
            courtValue = ""
            courtDisabled = true;
        } else {
            courtValue = data[0].number.toString();
            courtDisabled = false;
        }

        this.setState({
            courtList: courtList,
            courtValue: courtValue,
            courtDisabled: courtDisabled
        })
    }

    venueHandler = (event) => {
        this.setState({
            venueValue: event.target.value
        })

        getCourtWithVenue(this.getCourtWithVenueCallback, event.target.value)
    }

    courtHandler = (event) => {
        this.setState({
            courtValue: event.target.value
        })
    }

    playerModalOpen = () => {
        this.setState({
            player: true
        })
    }

    playerModalClose = () => {
        this.setState({
            player: false
        })
    }

    matchModalOpen = () => {
        this.setState({
            match: true
        })
    }

    matchModalClose = () => {
        this.setState({
            match: false
        })
    }

    courtModalOpen = () => {
        this.setState({
            court: true
        })
    }

    courtModalClose = () => {
        this.setState({
            court: false
        })
    }

    venueModalOpen = () => {
        this.setState({
            venue: true
        })
    }

    venueModalClose = () => {
        this.setState({
            venue: false
        })
    }

    getVenuesCallback = (data) => {
        let venueList = []

        data.forEach(element => {
            venueList.push(
                <MenuItem key={element.name} value={element.name}>{element.name} ({element.address})</MenuItem>
            )
        })

        this.setState({
            venuesList: venueList
        })
    }

    getLastMatchIDCallback = (data) => {
        this.setState({
            matchIndex: data + 1
        })

        getVenues(this.getVenuesCallback)
    }

    getSimplePlayersCallback = (data) => {
        let playerList = []


        data.forEach(element => {
            playerList.push(
                <MenuItem key={element.email} value={element.email}>{element.email} - {element.surname}</MenuItem>
            )
        })

        this.setState({
            playersList: playerList
        })

        getLastMatchID(this.getLastMatchIDCallback)
    }

    componentDidMount() {
        getSimplePlayers(this.getSimplePlayersCallback);

    }

    render() {
        return (
            <div className={styles.admin}>
                <div className={styles.cardContainer}>
                    <Card sx={{ width: "450px", padding: "15px 50px" }}>
                        <CardContent>
                            <div className={styles.playerHeader}>Player</div>
                            <div className={styles.playerSub}>Add a new player</div>
                            <div className={styles.buttonContainer}>
                                <Button variant="contained" color="success" onClick={this.playerModalOpen}>
                                    open
                                </Button>
                            </div>
                        </CardContent>
                    </Card>
                    <Card sx={{ width: "450px", padding: "15px 50px" }}>
                        <CardContent>
                            <div className={styles.playerHeader}>Match</div>
                            <div className={styles.playerSub}>Add a new match</div>
                            <div className={styles.buttonContainer}>
                                <Button variant="contained" color="success" onClick={this.matchModalOpen}>
                                    open
                                </Button>
                            </div>
                        </CardContent>
                    </Card>
                </div>
                <div className={styles.cardContainer}>
                    <Card sx={{ width: "450px", padding: "15px 50px" }}>
                        <CardContent>
                            <div className={styles.playerHeader}>Venue</div>
                            <div className={styles.playerSub}>Add a new venue</div>
                            <div className={styles.buttonContainer}>
                                <Button variant="contained" color="success" onClick={this.venueModalOpen}>
                                    open
                                </Button>
                            </div>
                        </CardContent>
                    </Card>
                    <Card sx={{ width: "450px", padding: "15px 50px" }}>
                        <CardContent>
                            <div className={styles.playerHeader}>Court</div>
                            <div className={styles.playerSub}>Add a new court</div>
                            <div className={styles.buttonContainer}>
                                <Button variant="contained" color="success" onClick={this.courtModalOpen}>
                                    open
                                </Button>
                            </div>
                        </CardContent>
                    </Card>
                </div>
                <Modal
                    open={this.state.player}
                    onClose={this.playerModalClose}
                >
                    <Card sx={{
                        position: 'absolute',
                        width: "600px",
                        height: "700px",
                        padding: "15px 50px",
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        overflowY: 'auto'
                    }}>
                        <CardContent>
                            <div className={styles.playerHeader}>Player</div>
                            <div className={styles.playerSub}>Add a new player</div>
                            <div className={styles.inputContainer}>
                                <TextField
                                    required
                                    label="email"
                                    margin="normal"
                                    color="success"
                                    onChange={(event) => this.inputHandler(event, "email")}
                                />

                                <TextField
                                    required
                                    label="Forename"
                                    margin="normal"
                                    color="success"
                                    onChange={(event) => this.inputHandler(event, "forename")}
                                />
                                <TextField
                                    label="Middle names"
                                    margin="normal"
                                    color="success"
                                    onChange={(event) => this.inputHandler(event, "middlenames")}
                                />

                                <TextField
                                    required
                                    id="outlined"
                                    label="Surname"
                                    margin="normal"
                                    color="success"
                                    onChange={(event) => this.inputHandler(event, "surname")}
                                />

                                <TextField
                                    required
                                    id="outlined"
                                    label="Date of Birth (DD/MM/YYYY)"
                                    margin="normal"
                                    color="success"
                                    onChange={(event) => this.inputHandler(event, "dateofbirth")}
                                />
                                <TextField
                                    required
                                    label="ELO"
                                    margin="normal"
                                    color="success"
                                    defaultValue={1000}
                                    type="number"
                                    disabled
                                />
                                {this.state.phoneHandler}
                            </div>
                            <div>
                                <Button variant="contained" color="success" onClick={this.addNewPhone}>
                                    add new phone
                                </Button>
                                <Button variant="contained" color="success" onClick={this.playerModalOpen}>
                                    create
                                </Button>
                            </div>
                        </CardContent>
                    </Card>
                </Modal>


                <Modal
                    open={this.state.match}
                    onClose={this.matchModalClose}
                >
                    <Card sx={{
                        position: 'absolute',
                        width: "600px",
                        padding: "15px 50px",
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                    }}>
                        <CardContent>
                            <div className={styles.playerHeader}>Match</div>
                            <div className={styles.playerSub}>Add a new match</div>
                            <div className={styles.inputContainer}>
                                <FormControl fullWidth>
                                    <InputLabel id="player1">Player 1</InputLabel>
                                    <Select labelId="player1" label="Player 1" value={this.state.matchP1} onChange={this.user1Handler}>
                                        {this.state.playersList}
                                    </Select>
                                </FormControl>
                                <div style={{ paddingBottom: "20px" }} />
                                <FormControl fullWidth>
                                    <InputLabel id="player2">Player 2</InputLabel>
                                    <Select labelId="player2" label="Player 2" value={this.state.matchP2} onChange={this.user2Handler}>
                                        {this.state.playersList}
                                    </Select>
                                </FormControl>

                                <TextField
                                    required
                                    label="Date of Play (DD/MM/YYYY)"
                                    margin="normal"
                                    color="success"
                                />
                                <div style={{ paddingBottom: "20px" }} />

                                <FormControl fullWidth>
                                    <InputLabel id="venue">Venue (Address)</InputLabel>
                                    <Select key="vvenue" labelId="venue" label="Venue (Address)" value={this.state.venueValue} onChange={this.venueHandler}>
                                        {this.state.venuesList}
                                    </Select>
                                </FormControl>
                                <div style={{ paddingBottom: "20px" }} />
                                <FormControl fullWidth disabled={this.state.courtDisabled}>
                                    <InputLabel id="court">{this.state.courtDisabled ? "No court in this venue" : "Court (Surface Type)"}</InputLabel>
                                    <Select key="dcourt" labelId="court" label={this.state.courtDisabled ? "No court in this venue" : "Court (Surface Type)"} value={this.state.courtValue} onChange={this.courtHandler}>
                                        {this.state.courtList}
                                    </Select>
                                </FormControl>


                                <div className={styles.setScoreContainer}>
                                    <div className={styles.eachSetContainer}>
                                        <div className={styles.setHeader}>Set 1</div>
                                        <TextField
                                            required
                                            fullWidth
                                            label="Player 1 Score"
                                            margin="normal"
                                            color="success"
                                            type="number"
                                        />

                                        <TextField
                                            required
                                            fullWidth
                                            label="Player 2 Score"
                                            margin="normal"
                                            color="success"
                                            type="number"
                                        />
                                    </div>
                                    <div className={styles.eachSetContainer}>
                                        <div className={styles.setHeader}>Set 2</div>
                                        <TextField
                                            required
                                            fullWidth
                                            label="Player 1 Score"
                                            margin="normal"
                                            color="success"
                                            type="number"
                                        />

                                        <TextField
                                            fullWidth
                                            required
                                            label="Player 2 Score"
                                            margin="normal"
                                            color="success"
                                            type="number"
                                        />
                                    </div>
                                    <div className={styles.eachSetContainer}>
                                        <div className={styles.setHeader}>Set 3</div>
                                        <TextField
                                            required
                                            fullWidth
                                            label="Player 1 Score"
                                            margin="normal"
                                            color="success"
                                            type="number"
                                        />

                                        <TextField
                                            required
                                            fullWidth
                                            label="Player 2 Score"
                                            margin="normal"
                                            color="success"
                                            type="number"
                                        />
                                    </div>
                                </div>
                            </div>
                            <div>
                                <Button variant="contained" color="success" onClick={this.playerModalOpen}>
                                    create
                                </Button>
                            </div>
                        </CardContent>
