import React from "react";
import { getMatches, getFullName } from "../../../axios/axios";
import { Card } from "@mui/material";
import { CardContent } from "@mui/material";
import styles from './MatchPage.module.css'
import { element } from "prop-types";


class MatchDetailPage extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            matchPage: {},
            p1user: "",
            p1userELO: "",
            p2user: "",
            p2userELO: "",
            court: {
                number: 0,
                surface: "",
                venue: {
                    name: "",
                }
            },
            datePlayed: "",
            set:
                [
                    {
                        "setNumber": 1,
                        "p1Score": 0,
                        "p2Score": 0
                    },
                    {
                        "setNumber": 2,
                        "p1Score": 0,
                        "p2Score": 0
                    },
                    {
                        "setNumber": 3,
                        "p1Score": 0,
                        "p2Score": 0
                    }
                ],
            winner: 0,

        }
    }

    getFullNameCallback = (data, user) => {
        if (user === 1) {
            this.setState({
                p1user: data.fullName,
                p1userELO: data.elo
            })
        } else if (user === 2) {
            this.setState({
                p2user: data.fullName,
                p2userELO: data.elo
            })
        }
    }

    getMatchesCallback = (data) => {
        console.log(data)
        getFullName(this.getFullNameCallback, data[0].p1Email, 1)
        getFullName(this.getFullNameCallback, data[0].p2Email, 2)


        let p1 = 0;
        let p2 = 0;

        data[0].set.forEach(element => {
            if (element.p1Score > element.p2Score) {
                p1++;
            } else {
                p2++;
            }
        }
        )

        this.setState({
            court: data[0].court,
            datePlayed: data[0].datePlayed,
            set: data[0].set,
            winner: p1 > p2 ? 1 : 2
        })
    }

    componentDidMount() {
        getMatches(this.getMatchesCallback, "?id=" + this.props.match.params.id)
    }

    render() {
        return (
            <div className={styles.match}>
                <Card sx={{ width: "450px", padding: "10px 5px", borderRadius: "10px" }}>
                    <CardContent>
                        <div>
                            <div className={styles.matchTitle}>
                                <div>Match in {this.state.court.venue.name}</div>
                                <div>{this.state.datePlayed}</div>
                            </div>
                            <div className={styles.courtDetail}>
                                <div>Court: {this.state.court.number}</div>
                                <div>Type: {this.state.court.surface}</div>
                            </div>
                            <div className={styles.userDetails}>
                                <div>
                                    <div className={data[i].email === data[i].winner ? styles.winner : styles.plain}>{this.state.p1userELO}<div className={data[i].email === data[i].winner ? styles.arrowUp : styles.arrowDown} /></div>
                                    <div className={data[i].email === data[i].winner ? styles.winner : styles.plain}>{this.state.p1user}</div>

                                </div>
                                <div className={styles.versus}>VS</div>
                                <div>
                                    <div className={data[i + 1].email === data[i].winner ? styles.winner : styles.plain}>{this.state.p2userELO}<div className={data[i + 1].email === data[i].winner ? styles.arrowUp : styles.arrowDown} /></div>
                                    <div className={data[i + 1].email === data[i].winner ? styles.winner : styles.plain}>{this.state.p2user}</div>
                                </div>
                            </div>
                        </div>
                    </CardContent>
                </Card>
            </div>
        )
    }

}

export default MatchDetailPage;
