import React from "react";
import { getMatches } from "../../../axios/axios";
import Card from '../../../components/Card/Card.jsx'
import main from '../../images/main.jpg'
import styles from './MainPage.module.css'

class MainPage extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            mainPage: null
        }
    }

    getMatchesCallback = (data) => {
        let mainPage = []
        let p1Win = 0;
        data.forEach(element => {
            let sets = []

            element.set.forEach(set => {
                if (set.p1Score > set.p2Score) {
                    p1Win++;
                }
            })

            element.set.forEach(set => {
                sets.push(
                    <div key={element.id + "-" + set.setNumber} className={styles.setCircle}>
                        <div className={styles.p1score}>{set.p1Score}</div>
                        <div className={styles.result}>{set.p1Score > set.p2Score ? "W" : "L"}</div>
                        <div className={styles.p2score}>{set.p2Score}</div>
                    </div>
                )
            })

            mainPage.push(
                <Card styleName="cardWhite round relative shadow" inlineStyles={{ width: "100%", marginBottom: "40px", display: "flex", flexDirection: "column" }} key={element.id}>
                    <div className={styles.header}>
                        <div className={styles.date}>
                            {element.datePlayed}
                        </div>
                        <div className={styles.court}>
                            in {element.court.venue.name}
                        </div>
                    </div>
                    <div className={styles.users}>
                        <div className={p1Win >= 2 ? styles.winner : styles.loser}>{element.p1Email}</div>
                        <div className={styles.versus}>vs</div>
                        <div className={p1Win >= 2 ? styles.loser : styles.winner}>{element.p2Email}</div>
                    </div>
                    <div className={styles.setsCont}>
                        {sets}
                    </div>
                </Card>
            )
        });

        this.setState({
            mainPage: mainPage
        })
    }

    componentDidMount() {
        getMatches(this.getMatchesCallback)
    }

    render() {
        return (
            <div>
                <img className={styles.image} src={main} alt="main" />
                <div className={styles.greeting}>
                    Get updates on the latest Tennis action in one place!
                </div>
                <div className={styles.main}>
                    <div className={styles.latestHeader}>
                        Latest Match
                    </div>
                    {this.state.mainPage}
                </div>
            </div>
        )
    }

}

export default MainPage;
