import { Card } from "@mui/material";
import { CardContent } from "@mui/material";
import { Modal } from "@mui/material";
import { InputLabel } from "@mui/material";
import { FormControl } from "@mui/material";
import Select from '@mui/material/Select';

import { Button } from "@mui/material";
import { TextField } from "@mui/material";
import React from "react";
import { getSimplePlayers, getLastMatchID, getVenues, getCourtWithVenue, createPlayer, createNewMatch, createNewCourt, createNewVenue } from "../../../axios/axios";

import styles from './AdminPage.module.css'
import { MenuItem } from "@mui/material";
import { Autocomplete } from "@mui/material";


class AdminPage extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            player: false,
            match: false,
            court: false,
            venue: false,

            phoneHandler: [
                <div key={1}>
                    <TextField
                        fullWidth
                        label={"phone" + 1}
                        margin="normal"
                        color="success"
                        onChange={(event) => this.phoneHandler(event, 0)}
                    />
                    <Autocomplete
                        onChange={(event, value) => this.phoneTypeHandler(value, 0)}
                        disablePortal
                        id="test"
                        options={
                            [
                                {
                                    label: "Home",
                                    value: "home"
                                },
                                {
                                    label: "Mobile",
                                    value: "mobile"
                                },
                                {
                                    label: "Work",
                                    value: "work"
                                },
                            ]
                        }
                        renderInput={(params) => <TextField {...params} label={"Phone" + (1) + " Type"} />}
                    />
                </div>
            ],

            email: "",
            forename: "",
            middleNames: "",
            surname: "",
            dateOfBirth: "",
            phones: [
                {
                    phoneNumber: "",
                    phoneType: "",
                }
            ],

            playersList: "",

            matchP1: "",
            matchP2: "",
            matchDate: "",
            venueValue: "",
            courtValue: "",
            courtSufcae: "",
            set1P1Value: "",
            set1P2Value: "",
            set2P1Value: "",
            set2P2Value: "",
            set3P1Value: "",
            set3P2Value: "",


            matchIndex: 0,

            addVenue: "",
            addVenueAddress: "",


            venuesList: "",
            courtList: "",


            addCourtSurface: "grass",
            newCourtNumber: 0,

            courtDisabled: true,
        }
    }

    inputHandler = (event, toChange) => {
        this.setState({
            [toChange]: event.target.value
        })
    }

    phoneHandler = (event, phoneIndex) => {
        let phones = [...this.state.phones];

        phones[phoneIndex].phoneNumber = event.target.value;

        this.setState({
            phones: [...phones]
        })
    }

    phoneTypeHandler = (value, phoneIndex) => {
        let phoneHandler = [...this.state.phoneHandler];

        let phones = [...this.state.phones];

        phones[phoneIndex].phoneType = value.value;

        this.setState({
            phones: [...phones],
            phoneHandler: phoneHandler
        })
    }

    addNewPhone = () => {
        let phoneHandler = [...this.state.phoneHandler];
        let newIndex = this.state.phoneHandler.length + 1;
        phoneHandler.push(
            <div key={newIndex}>
                <TextField
                    fullWidth
                    label={"phone" + newIndex}
                    margin="normal"
                    color="success"
                    onChange={(event) => this.phoneHandler(event, newIndex - 1)}
                />
                <Autocomplete
                    onChange={(event, value) => this.phoneTypeHandler(value, newIndex - 1)}
                    disablePortal
                    id="test"
                    options={
                        [
                            {
                                label: "Home",
                                value: "home"
                            },
                            {
                                label: "Mobile",
                                value: "mobile"
                            },
                            {
                                label: "Work",
                                value: "work"
                            },
                        ]
                    }
                    renderInput={(params) => <TextField {...params} label={"Phone" + (newIndex) + " Type"} />}
                />
